ind_liquidez$liq_seca &lt;- formatC(ind_liquidez$liq_seca, format = "f", digits = 2, decimal.mark = ",", big.mark = ".", width = NULL)
ind_liquidez$liq_imediata &lt;- formatC(ind_liquidez$liq_imediata, format = "f", digits = 2, decimal.mark = ",", big.mark = ".", width = NULL)

DT::datatable(
  ind_liquidez, 
  extensions = 'FixedHeader',
  rownames = F,
  colnames = c('Empresa' = 'EMPRESA',
               'Liquidez Geral' = "liq_geral", 
               'Liquidez Corrente' = "liq_corrente",
               'Liquidez Seca' = "liq_seca",
               'Liquidez Imediata' = "liq_imediata"),
  options = list(
    pageLength = 10,
    initComplete = JS(
      "function(settings, json) {",
      "$(this.api().table().header()).css({
      'background-color': '#B52730', 
      'color': '#fff'});","}"),
    width = '100%',
    align = 'center'
    ),
  elementId = "ind_liquidez"
)

